import{a as t}from"./chunk-YMOFM5P6.js";import{m as c}from"./chunk-2NRCEUGS.js";import{Z as s,ca as p,k as n,s as o}from"./chunk-77XINQMO.js";var b=(()=>{class e{constructor(a){this.router=a,this.appPaths=t,this.activeTherapistIndex=new n(0),this.therapists=[{id:0,seoData:{title:"Paulina Okrasa-Burczyk - Terapeuta integracji seonsorycznej",metaDescription:"Paulina Okrasa-Burczyk - za\u0142o\u017Cycielka Centrum R\xF3wnowaga w Kaliszu, psycholog, pedagog, terapeutka SI i INPP, specjalistka w pracy z dzie\u0107mi w spektrum autyzmu."},name:"Paulina Okrasa-Burczyk",role:"Za\u0142o\u017Cycielka Centrum R\xF3wnowaga, pedago\u017Cka, terapeutka integracji&nbsp;sensorycznej, terapeutka neurorozwojowa metody&nbsp;INPP i&nbsp;przetwarzania s\u0142uchowego, socjoterapeutka, terapeutka dzieci w&nbsp;spektrum autyzmu i&nbsp;z&nbsp;niepe\u0142nosprawno\u015Bci\u0105 intelektualn\u0105, logopedka",webpImg:"assets/img/Paulina-Okrasa-Burczyk.webp",alterImg:"assets/img/Paulina-Okrasa-Burczyk.jpg",assistance:["Diagnoza i&nbsp;terapia integracji sensorycznej","Diagnoza i&nbsp;terapia neurorozwojowa odruch\xF3w&nbsp;INPP","Diagnoza i&nbsp;terapia r\u0119ki","Diagnoza i&nbsp;terapia przetwarzania s\u0142uchowego Johansena","Terapia NeuroTaktylna&nbsp;MNRI\xAE","Konsultacje sensomotoryczne i&nbsp;neurorozwojowe dla&nbsp;rodzic\xF3w"],aboutMe:`
    Na&nbsp;co&nbsp;dzie\u0144 wspieram dzieci w&nbsp;prawid\u0142owym rozwoju sensomotorycznym i&nbsp;neuropsychologicznym. Podczas
    przeprowadzania diagnoz i&nbsp;terapii staram si\u0119 patrze\u0107 na&nbsp;dziecko i&nbsp;jego najbli\u017Csze otoczenie w&nbsp;holistyczny
    i&nbsp;ca\u0142o\u015Bciowy spos\xF3b, co&nbsp;umo\u017Cliwia mi&nbsp;wyb\xF3r najlepszych form i&nbsp;metod wsparcia dla&nbsp;konkretnych trudno\u015Bci i&nbsp;wyzwa\u0144,
    z&nbsp;kt\xF3rymi zmagaj\u0105 si\u0119 dzieci i&nbsp;ich rodzice.
    `,sections:[{title:"Holistyczne podej\u015Bcie do&nbsp;terapii",content:"Moje podej\u015Bcie obejmuje ca\u0142o\u015Bciowe spojrzenie na&nbsp;dziecko i&nbsp;jego otoczenie. W&nbsp;razie potrzeby kieruj\u0119 dzieci na&nbsp;pog\u0142\u0119bion\u0105 diagnostyk\u0119 z&nbsp;zakresu neurologopedii, fizjoterapii, osteopatii, konsultacji psychologicznych, psychoterapii czy&nbsp;psychodietetyki."},{title:"Relacja z&nbsp;dzieckiem \u2013 klucz do&nbsp;sukcesu",content:"Najwa\u017Cniejszym elementem terapii jest dla&nbsp;mnie nawi\u0105zanie relacji z&nbsp;dzieckiem, aby&nbsp;czu\u0142o si\u0119 komfortowo i&nbsp;bezpiecznie. Tylko w&nbsp;taki spos\xF3b terapia mo\u017Ce przynosi\u0107 oczekiwane efekty."},{title:"Nieustanny rozw\xF3j zawodowy",content:"Ceni\u0119 rozw\xF3j i&nbsp;stale podnosz\u0119 swoje kwalifikacje, aby&nbsp;m\xF3c jak&nbsp;najskuteczniej pomaga\u0107 naszym ma\u0142ym klientom. Pracuj\u0119 tak\u017Ce z&nbsp;dzie\u0107mi z&nbsp;r&oacute;\u017Cnego rodzaju niepe\u0142nosprawno\u015Bciami oraz&nbsp;w&nbsp;spektrum autyzmu."},{title:"Do\u015Bwiadczenie zawodowe",content:"Do\u015Bwiadczenie w&nbsp;pracy z&nbsp;dzie\u0107mi zdobywa\u0142am pracuj\u0105c kilka lat w&nbsp;specjalnym o\u015Brodku wychowawczym dla&nbsp;ch\u0142opc\xF3w z&nbsp;niepe\u0142nosprawno\u015Bci\u0105 intelektualn\u0105 oraz&nbsp;w&nbsp;szkole og\xF3lnodost\u0119pnej jako nauczyciel wspomagaj\u0105cy dzieci w&nbsp;spektrum autyzmu oraz&nbsp;jako polonistka. Obecnie poza&nbsp;dzia\u0142alno\u015Bci\u0105 w&nbsp;Centrum R\xF3wnowaga jestem tak\u017Ce nauczycielem w&nbsp;szkole specjalnej, gdzie&nbsp;pracuj\u0119 z&nbsp;dzie\u0107mi z&nbsp;wszelkiego rodzaju niepe\u0142nosprawno\u015Bciami. Prowadz\u0119 tak\u017Ce terapi\u0119 w&nbsp;zakresie Wczesnego Wspomagania Dziecka."},{title:"Zainteresowania naukowe",content:"Interesuj\u0119 si\u0119 psychologi\u0105, szczeg\xF3lnie w&nbsp;kontek\u015Bcie traumy rozwojowej, neuropsychologii, psychoterapii sensomotorycznej oraz&nbsp;Teorii poliwagalnej Stephena Porgesa. Uwa\u017Cam, \u017Ce&nbsp;umys\u0142 i&nbsp;cia\u0142o to&nbsp;jedno\u015B\u0107, kt\xF3r\u0105 nale\u017Cy traktowa\u0107 z&nbsp;pe\u0142nym zrozumieniem i&nbsp;nigdy nie&nbsp;zapomina\u0107, \u017Ce&nbsp;ca\u0142y czas na&nbsp;siebie wzajemnie oddzia\u0142uj\u0105."},{title:"Prywatnie",content:"W&nbsp;\u017Cyciu prywatnym jestem w\u0142a\u015Bcicielk\u0105 trzech kundelk\xF3w \u2013 adopciak\xF3w, kt\xF3re&nbsp;ka\u017Cdego dnia dostarczaj\u0105 mi&nbsp;wsparcia w&nbsp;postaci dogoterapii. Gor\u0105co polecam adopcj\u0119 \u2013 Nie&nbsp;kupuj, adoptuj! :)"}],qualifications:{education:["Filologia polska z&nbsp;pedagogik\u0105 \u2013 studia magisterskie, Uniwersytet Wroc\u0142awski","Logopedia og\xF3lna \u2013 studia podyplomowe, Uniwersytet Wroc\u0142awski","Oligofrenopedagogika \u2013 studia podyplomowe","Resocjalizacja i&nbsp;socjoterapia \u2013 studia podyplomowe","Wspieranie rozwoju i&nbsp;edukacja os\xF3b ze&nbsp;spektrum autyzmu \u2013 studia podyplomowe","Integracja sensoryczna \u2013 studia podyplomowe, Wy\u017Csza Szko\u0142a Edukacja w&nbsp;Sporcie, Instytut Sportu i&nbsp;Rekreacji w&nbsp;Warszawie","Psychologia \u2013 studia magisterskie (w&nbsp;trakcie), Akademia Ekonomiczno-Humanistyczna w&nbsp;Warszawie"],certificates:["Klinika Integracji Sensorycznej \u2013 certyfikat terapeuty i&nbsp;diagnosty integracji sensorycznej, Centrum Integracji Sensorycznej w&nbsp;Warszawie","Johansen Indywidualna Stymulacja S\u0142uchu \u2013 indywidualny program treningu s\u0142uchowego, certyfikacja jako Provider JIAS, K.&nbsp;Rychetsky, R.&nbsp;Borowiecka, Wroc\u0142aw"],specialistTrainings:["Terapia r\u0119ki i&nbsp;zaburze\u0144 motoryki ma\u0142ej I&nbsp;i&nbsp;II&nbsp;stopnia, diagnoza \u2013 terapia \u2013 masa\u017C, O\u015Brodek ProCentrum, Wroc\u0142aw","Apteczka Pierwszej Pomocy Emocjonalnej \u2013 kurs w&nbsp;zakresie psychologii pozytywnej, ODN w&nbsp;Kaliszu","Trening Umiej\u0119tno\u015Bci Spo\u0142ecznych TUS, terapia grupowa dzieci ze&nbsp;spektrum autyzmu, Fundacja Pomoc Autyzm, Warszawa","Integracja NeuroTaktylna MNRI\xAE \u2013 Svetlana Masgutowa Educational Institute, szkolenie w&nbsp;O\u015Brodku ASTO, Wroc\u0142aw","Szkolenie Bazowe Pozytywnej Dyscypliny dla&nbsp;Edukacji, ODN Kalisz","Diagnoza i&nbsp;terapia op\xF3\u017Anienia neurorozwojowego \u2013 roczne szkolenie programu indywidualnego The Institute for Neuro-Psychological Psychology, Chester (filia: INPP Polska, Warszawa)"],sensoryIntegrationTrainings:["Wspomaganie rozwoju dzieci ze&nbsp;specyficznymi trudno\u015Bciami w&nbsp;nauce \u2013 Szkolny Program Interwencyjny Sally Goddard Blythe, Instytut Psychologii Neurofizjologicznej INPP Polska, Maria Matuszkiewicz","Nadreaktywno\u015B\u0107 i&nbsp;Podreaktywno\u015B\u0107 sensoryczna w&nbsp;terapii SI, szkolenie Ma\u0142gorzata Karga","Integracja Sensoryczna a&nbsp;ADHD, szkolenie Z.&nbsp;Przyrowski","Chodzenie na&nbsp;palcach u&nbsp;dzieci: kiedy norma a&nbsp;kiedy zaburzenie, szkolenie W.&nbsp;Koz\u0142owski, B.&nbsp;Witkowska","Sensoplastyka, szkolenie Inkubator Inspiracji","Diagnoza Proces\xF3w Integracji Sensorycznej, Fundacja na&nbsp;Rzecz Rodzin Dzieci z&nbsp;Autyzmem i&nbsp;Innymi Dysfunkcjami Okno","Diagnoza i&nbsp;terapia SI ma\u0142ego dziecka, Fundacja na&nbsp;Rzecz Rodzin Dzieci z&nbsp;Autyzmem i&nbsp;Innymi Dysfunkcjami Okno","Plany terapii w&nbsp;SI, Fundacja na&nbsp;Rzecz Rodzin Dzieci z&nbsp;Autyzmem i&nbsp;Innymi Dysfunkcjami Okno","Obserwacja Kliniczna SI w&nbsp;aspekcie fizjoterapeutycznym, Fundacja na&nbsp;Rzecz Rodzin Dzieci z&nbsp;Autyzmem i&nbsp;Innymi Dysfunkcjami Okno","Ocena napi\u0119cia mi\u0119\u015Bniowego i&nbsp;postawy dziecka w&nbsp;korelacji z&nbsp;zaburzeniami sensorycznymi, szkolenie Fundacja Promyk S\u0142o\u0144ca, prowadz\u0105cy W.&nbsp;Koz\u0142owski","Teoria poliwagalna i&nbsp;integracja sensoryczna w&nbsp;praktyce, badanie zale\u017Cno\u015Bci w&nbsp;teorii i&nbsp;wskazania do&nbsp;terapii, szkolenie Fundacja Promyk S\u0142o\u0144ca, prowadz\u0105ca Tracy Murnan Stackhouse"]},znanyLekarz:"https://www.znanylekarz.pl/paulina-okrasa/logopeda/kalisz#profile-reviews"},{id:1,seoData:{title:"Sylwia Szumska - Terapeuta R\xF3wnowaga",metaDescription:"Sylwia Szumska - pedagog, terapeutka SI i specjalistka terapii pedagogicznej w Centrum R\xF3wnowaga w Kaliszu. Pomaga dzieciom z trudno\u015Bciami w nauce i rozwoju."},name:"Sylwia Szumska",role:"Pedago\u017Cka, terapeutka pedagogiczna, oligofrenopedagog, terapeutka dzieci z&nbsp;zaburzeniami ze&nbsp;spektrum autyzmu, terapeutka integracji&nbsp;sensorycznejcji, terapeutka r\u0119ki I&nbsp;i&nbsp;II stopnia",webpImg:"assets/img/awatar-damski.webp",alterImg:"assets/img/awatar-damski.png",assistance:["Terapia integracji sensorycznej (SI)","Terapia pedagogiczna dla dzieci z&nbsp;trudno\u015Bciami w&nbsp;nauce","Wsparcie dzieci z&nbsp;zaburzeniami ze&nbsp;spektrum autyzmu","Terapia r\u0119ki I&nbsp;i&nbsp;II stopnia","Terapia dzieci z&nbsp;niepe\u0142nosprawno\u015Bci\u0105 intelektualn\u0105","Socjoterapia \u2013 wsparcie w&nbsp;rozwijaniu umiej\u0119tno\u015Bci spo\u0142ecznych i&nbsp;emocjonalnych","Zaj\u0119cia sensomotoryczne wspomagaj\u0105ce koordynacj\u0119 ruchow\u0105 i&nbsp;motoryk\u0119","Wspomaganie rozwoju dzieci z&nbsp;trudno\u015Bciami szkolnymi (dysleksja, dysgrafia, dyskalkulia)","Konsultacje dla rodzic\xF3w dotycz\u0105ce rozwoju dziecka i&nbsp;metod wsparcia","Terapia zaburze\u0144 emocjonalnych i&nbsp;spo\u0142ecznych u&nbsp;dzieci"],aboutMe:`
      Praca z&nbsp;dzie\u0107mi jest moim powo\u0142aniem i&nbsp;\u015Bwiadomym wyborem. Jestem pasjonatk\u0105 pracy z&nbsp;dzie\u0107mi, kt\xF3ra
      daje mi&nbsp;wiele rado\u015Bci, bo&nbsp;mog\u0119 dzieli\u0107 si\u0119 wszystkim, co&nbsp;pomaga w&nbsp;ich funkcjonowaniu i&nbsp;co&nbsp;najwa\u017Cniejsze,
      przynosi u\u015Bmiech. U\u015Bmiechni\u0119te dziecko, u\u015Bmiechni\u0119ty terapeuta to&nbsp;u\u015Bmiechni\u0119ty i&nbsp;lepszy \u015Bwiat. W&nbsp;swoich
      dzia\u0142aniach kieruj\u0119 si\u0119 mottem zaczerpni\u0119tym od&nbsp;B.Cage, \u017Ce \u201ENauczyciel widzi wi\u0119cej ni\u017C tylko twarze dzieci \u2013 stara si\u0119 zobaczy\u0107 ich dusze\u201D.
    `,sections:[{title:"Nieustanny rozw\xF3j zawodowy \u2013 zdobywanie nowych do\u015Bwiadcze\u0144",content:`
          Pracuj\u0105c z&nbsp;dzie\u0107mi z&nbsp;zaburzeniami ze&nbsp;spektrum autyzmu, nieustannie dbam o&nbsp;sw\xF3j rozw\xF3j,
          uczestnicz\u0105c w&nbsp;r&oacute;\u017Cnego rodzaju szkoleniach, seminariach, kursach, webinariach, projektach i&nbsp;wolontariatach.
          <br><br>
          Dodatkowo, wykorzystuj\u0105c swoj\u0105 pasj\u0119 do&nbsp;pracy z&nbsp;dzie\u0107mi, pracowa\u0142am jako:
          <ul>
            <li>Wolontariusz w&nbsp;ramach projektu hipoterapii \u201E\u0106wicz dla&nbsp;siebie. Nie&nbsp;przestawaj. Trenuj\u201D realizowanego przez
            Pleszewskie Stowarzyszenie na&nbsp;Rzecz Os&oacute;b Niepe\u0142nosprawnych \u201ERazem w&nbsp;Przysz\u0142o\u015B\u0107\u201D</li>
            <li>Opiekun zaj\u0119\u0107 organizowanych przez Miasto Kalisz w&nbsp;ramach \u201EFerii Zimowych w&nbsp;Mie\u015Bcie dla&nbsp;dzieci kl. I-IV\u201D w&nbsp;Szkole Podstawowej nr&nbsp;1</li>
            <li>Instruktor p&oacute;\u0142kolonii letnich w&nbsp;Klubie Osiedlowym \u201EBlaszak\u201D Kaliskiej Sp&oacute;\u0142dzielni Mieszkaniowej w&nbsp;Kaliszu</li>
            <li>Instruktor p&oacute;\u0142kolonii letnich w&nbsp;Klubie Osiedlowym XXV-lecia Kaliskiej Sp&oacute;\u0142dzielni Mieszkaniowej w&nbsp;Kaliszu</li>
          </ul>
        `},{title:"Do\u015Bwiadczenie&nbsp;zawodowe",content:`
          Do\u015Bwiadczenie w&nbsp;pracy z&nbsp;dzie\u0107mi z&nbsp;niepe\u0142nosprawno\u015Bci\u0105 intelektualn\u0105 zdoby\u0142am, praktykuj\u0105c w&nbsp;Zespole
          Plac\xF3wek Specjalnych w&nbsp;Pleszewie, a&nbsp;nast\u0119pnie rozpocz\u0119\u0142am prac\u0119 zawodow\u0105 i&nbsp;terapeutyczn\u0105 w&nbsp;szko\u0142ach
          og\xF3lnodost\u0119pnych jako nauczyciel wsp&oacute;\u0142organizuj\u0105cy kszta\u0142cenie dzieci ze&nbsp;spektrum autyzmu, terapi\u0119
          pedagogiczn\u0105 oraz&nbsp;zaj\u0119cia integracji sensorycznej. Obecnie, poza&nbsp;prac\u0105 terapeuty integracji sensorycznej
          w&nbsp;Centrum R&oacute;wnowaga, pracuj\u0119 w&nbsp;Szkole Podstawowej nr&nbsp;18 z&nbsp;oddzia\u0142ami dla&nbsp;Dzieci z&nbsp;Autyzmem,
          gdzie&nbsp;pracuj\u0119 jako nauczyciel wychowawca klasy 3 dzieci z&nbsp;zaburzeniami ze&nbsp;spektrum autyzmu.
        `},{title:"Prywatnie",content:`
          W&nbsp;\u017Cyciu prywatnym czas wolny sp\u0119dzam z&nbsp;rodzin\u0105, naszym domowym pupilem kr&oacute;likiem Hepikiem, przyjaci\xF3\u0142mi
          i&nbsp;muzyk\u0105. Kocham taniec \u2013 ta\u0144cz\u0119 bachat\u0119 i&nbsp;sals\u0119. Bardzo lubi\u0119 prace handmade, dekoracj\u0119 wn\u0119trz,
          podr&oacute;\u017Ce, d\u0142ugie spacery, prace w&nbsp;ogrodzie w\u015Br&oacute;d kwiat&oacute;w oraz&nbsp;sport, jak&nbsp;wycieczki rowerowe i&nbsp;narty
          bieg&oacute;wki.
        `}],qualifications:{education:["Niepubliczny O\u015Brodek Doskonalenia Nauczycieli COGNITUS.PL \u2013 Terapia r\u0119ki i&nbsp;stopy I oraz&nbsp;II stopnia","Uczelnia Nauk Spo\u0142ecznych w&nbsp;\u0141odzi \u2013 Studia Podyplomowe, Integracja Sensoryczna - diagnoza i&nbsp;terapia","Wy\u017Csza Szko\u0142a Bankowa w&nbsp;Poznaniu \u2013 Studia Podyplomowe, Edukacja i&nbsp;terapia os&oacute;b z&nbsp;zaburzeniami ze&nbsp;spektrum autyzmu","Collegium Da Vinci Pozna\u0144 \u2013 Studia Podyplomowe, Oligofrenopedagogika z&nbsp;elementami integracji sensorycznej","Gnie\u017Anie\u0144ska Wy\u017Csza Szko\u0142a Humanistyczno-Mened\u017Cerska \u201EMILENIUM\u201D \u2013 Studia Podyplomowe, Wychowanie wczesnoszkolne i&nbsp;przedszkolne","Politechnika Pozna\u0144ska \u2013 Studia Uzupe\u0142niaj\u0105ce Magisterskie, In\u017Cynieria Komunikacji Interpersonalnej i&nbsp;Public Relations"],specialistTrainings:["Webinar \u201EJak polepszy\u0107 sen u&nbsp;os&oacute;b ze&nbsp;spektrum autyzmu\u201D, Terapia Zaj\u0119ciowa Profesjonalnie","Webinar \u201ESprawozdanie pedagoga specjalnego\u201D, Specjalni.pl Niepubliczny O\u015Brodek Doskonalenia Nauczycieli","Szkolenie PBP Ksi\u0105\u017Cnica Pedagogiczna w&nbsp;Kaliszu \u201ETIK-owy nauczyciel \u2013 Genial.ly - cz\u0119\u015B\u0107 II\u201D","Seminarium ODN Kalisz: \u201EPomoc psychologiczno-pedagogiczna udzielana uczniom przez specjalist&oacute;w szkolnych\u201D","Szkolenie \u201EABC pracy nauczyciela wsp&oacute;\u0142organizuj\u0105cego proces kszta\u0142cenia uczni&oacute;w ze&nbsp;specjalnymi potrzebami edukacyjnymi\u201D"],certificates:["Integracja Sensoryczna \u2013 diagnoza i&nbsp;terapia, Uczelnia Nauk Spo\u0142ecznych w&nbsp;\u0141odzi","Terapia r\u0119ki i&nbsp;stopy I oraz&nbsp;II stopnia, Cognitus Niepubliczny O\u015Brodek Doskonalenia Nauczycieli","Certyfikat OSEhero, Og&oacute;lnopolski Projekt Edukacyjny OSEhero w&nbsp;roku szkolnym 2023/2024"]}},{id:2,seoData:{title:"Karolina Okrasa-Staszak - Psycholog Centrum R\xF3wnowaga",metaDescription:"Karolina Okrasa-Staszak - psycholog w Centrum R\xF3wnowaga w Kaliszu. Specjalizuje si\u0119 w wsparciu emocjonalnym i terapeutycznym dzieci i m\u0142odzie\u017Cy."},name:"Karolina Okrasa-Staszak",role:"Psycholog",webpImg:"assets/img/awatar-damski.webp",alterImg:"assets/img/awatar-damski.png",assistance:["Problemy z regulacj\u0105 emocji (np. l\u0119k, z\u0142o\u015B\u0107, smutek)","Stres i napi\u0119cie zwi\u0105zane z codziennym funkcjonowaniem jak i trudnymi zdarzeniami","Obni\u017Cona samoocena","Psychosomatyczne dolegliwo\u015Bci (np. b\xF3le brzucha bez przyczyn medycznych)","Trudno\u015Bci w nawi\u0105zywaniu i utrzymywaniu relacji r\xF3wie\u015Bniczych","Lepsze zrozumienie dziecka oraz jego potrzeb","Pomoc w przyjrzeniu si\u0119 relacji z dzieckiem","Wsparcie w trudno\u015Bciach rodzicielskich","Dob\xF3r odpowiedniej metody pomocy, z mo\u017Cliwo\u015Bci\u0105 skierowania do innych specjalist\xF3w"],aboutMe:`
    Ka\u017Cdy etap \u017Cycia, pocz\u0105wszy od narodzin, wi\u0105\u017Ce si\u0119 z r\xF3\u017Cnorodnymi wyzwaniami, kt\xF3re s\u0105 kluczowe dla dalszego rozwoju.
    Te wyzwania pomagaj\u0105 w zdobywaniu nowych umiej\u0119tno\u015Bci, w tym w obszarze zdrowia psychicznego. Ka\u017Cde dziecko ma swoje
    unikalne predyspozycje, wynikaj\u0105ce zar\xF3wno z jego indywidualnych cech, jak i z wp\u0142ywu otoczenia. Wspieram dzieci i
    m\u0142odzie\u017C w radzeniu sobie z tymi wyzwaniami, rozumiej\u0105c, \u017Ce ka\u017Cdy przypadek jest wyj\u0105tkowy.<br><br>

    W dzisiejszym, szybko zmieniaj\u0105cym si\u0119 \u015Bwiecie, dzieci i m\u0142odzie\u017C mierz\u0105 si\u0119 nie tylko z naturalnymi trudno\u015Bciami
    rozwojowymi, ale tak\u017Ce z wyzwaniami, kt\xF3re mog\u0105 wp\u0142ywa\u0107 na ich zdrowie psychiczne. Je\u015Bli dostrzegasz niepokoj\u0105ce
    objawy w funkcjonowaniu psychicznym swojego dziecka lub nastolatka, serdecznie zapraszam na konsultacj\u0119 w naszym
    centrum.
  `,sections:[{title:"Zakres pracy",content:`
        Moja praca obejmuje poradnictwo psychologiczne oraz wsparcie psychologiczne w formie spotka\u0144 terapeutycznych z
        dzie\u0107mi i nastolatkami. Pracuj\u0119 z dzie\u0107mi w wieku przedszkolnym i szkolnym. Wa\u017Cnym elementem mojej pracy s\u0105
        r\xF3wnie\u017C spotkania z rodzicami, kt\xF3rzy odgrywaj\u0105 kluczow\u0105 rol\u0119 w prawid\u0142owym rozwoju i wsparciu swoich dzieci. W
        przypadku m\u0142odszych dzieci moja praca koncentruje si\u0119 g\u0142\xF3wnie na wsp\xF3\u0142pracy z rodzicami/opiekunami.
      `},{title:"Do\u015Bwiadczenie zawodowe",content:`
          Do\u015Bwiadczenie zawodowe zdobywa\u0142am, pracuj\u0105c na oddzia\u0142ach szpitalnych, gdzie zajmowa\u0142am si\u0119 diagnoz\u0105 funkcji poznawczych oraz wsparciem pacjent\xF3w, zwracaj\u0105c szczeg\xF3ln\u0105 uwag\u0119 na relacje mi\u0119dzy cia\u0142em a psychik\u0105. Pracowa\u0142am r\xF3wnie\u017C w instytucjach pa\u0144stwowych, gdzie pomaga\u0142am pacjentom w r\xF3\u017Cnego rodzaju kryzysach psychologicznych.
          <br><br>
          Obecnie pracuj\u0119 w szkole podstawowej, gdzie prowadz\u0119 zaj\u0119cia indywidualne wspieraj\u0105ce rozw\xF3j emocjonalny i spo\u0142eczny dzieci oraz organizuj\u0119 warsztaty dla klas z zakresu profilaktyki zdrowia psychicznego.
      `}],qualifications:{education:["Psychologia (specjalno\u015B\u0107 psychologia kliniczna i seksuologia) 5-letnie studia magisterskie na Uniwersytecie im. Adama Mickiewicza w Poznaniu","Przygotowanie pedagogiczne - studia podyplomowe w Wy\u017Cszej Szkole Kszta\u0142cenia Zawodowego we Wroc\u0142awiu","Studium Psychoterapii Dzieci i M\u0142odzie\u017Cy - dwuletnie szkolenie w Instytucie Edukacji i Terapii w Poznaniu"]}},{id:3,seoData:{title:"Magdalena Mokrzycka - Specjalista R\xF3wnowaga",metaDescription:"Magdalena Mokrzycka - terapeutka SI w Centrum R\xF3wnowaga. Specjalizuje si\u0119 w integracji sensorycznej i wsparciu rozwoju dzieci w Kaliszu."},name:"Magdalena Mokrzycka",role:"Pedago\u017Cka, terapeutka integracji&nbsp;sensorycznej",webpImg:"assets/img/awatar-damski.webp",alterImg:"assets/img/awatar-damski.png",assistance:["Wsparcie w&nbsp;rozwijaniu umiej\u0119tno\u015Bci motorycznych i&nbsp;koordynacji ruchowej","Praca nad regulacj\u0105 reakcji sensorycznych (nadwra\u017Cliwo\u015B\u0107, podwra\u017Cliwo\u015B\u0107)","Wsparcie w&nbsp;poprawie koncentracji i&nbsp;uwagi","Praca nad rozwijaniem umiej\u0119tno\u015Bci adaptacyjnych i&nbsp;samoregulacji","Pomoc w&nbsp;kszta\u0142towaniu prawid\u0142owych odruch\xF3w i&nbsp;wzorc\xF3w ruchowych","Konsultacje i&nbsp;instrukta\u017C dla rodzic\xF3w w&nbsp;zakresie wspierania dziecka w&nbsp;domu","Wsparcie w&nbsp;integracji zmys\u0142\xF3w i&nbsp;poprawie codziennego funkcjonowania dziecka"],aboutMe:`
    Od&nbsp;zawsze chcia\u0142am pracowa\u0107 z&nbsp;dzie\u0107mi. Ju\u017C w&nbsp;dzieci\u0144stwie moj\u0105 ulubion\u0105 zabaw\u0105 by\u0142a w\u0142a\u015Bnie
    zabawa w&nbsp;szko\u0142\u0119. Lata nauki, studi\xF3w i&nbsp;praktyki potwierdzi\u0142y moje dziecinne marzenia, a&nbsp;rok 2024
    okaza\u0142 si\u0119 dla mnie prze\u0142omowy, poniewa\u017C uko\u0144czy\u0142am studia magisterskie z&nbsp;pedagogiki
    opieku\u0144czo-wychowawczej oraz studia podyplomowe z&nbsp;terapii integracji sensorycznej.
    Czuj\u0119 si\u0119 w&nbsp;pe\u0142ni wykwalifikowan\u0105 osob\u0105, aby pracowa\u0107 z&nbsp;dzie\u0107mi i&nbsp;zawsze d\u0105\u017Cy\u0107 do&nbsp;nich z&nbsp;pomoc\u0105, je\u015Bli jej
    wymagaj\u0105.
  `,sections:[{title:"Indywidualne podej\u015Bcie do&nbsp;ka\u017Cdego dziecka",content:`
        Ka\u017Cde dziecko jest inne. Ka\u017Cde ma swoje problemy i&nbsp;obawy. Dla&nbsp;nas pedagog\xF3w wa\u017Cne jest to, aby dotrze\u0107
        do&nbsp;ka\u017Cdego z&nbsp;nich i&nbsp;pom\xF3c w&nbsp;jak najlepszy spos\xF3b. Wa\u017Cne jest, aby ci\u0105gle budowa\u0107 w&nbsp;dziecku
        poczucie w\u0142asnej warto\u015Bci. Kluczem do&nbsp;sukcesu jest wypracowanie z&nbsp;dzieckiem indywidualnego sposobu
        pracy, kt\xF3ry pozwoli czerpa\u0107 dziecku rado\u015B\u0107 z&nbsp;pracy z&nbsp;pedagogiem.<br><br>

        Ka\u017Cde dziecko jest inne!<br>
        Ka\u017Cde dziecko jest wyj\u0105tkowe!<br>
        Ka\u017Cde dziecko jest pi\u0119kne!
      `},{title:"Sta\u0142e doskonalenie umiej\u0119tno\u015Bci",content:`
        W&nbsp;pedagogice nigdy nie&nbsp;mo\u017Cna spocz\u0105\u0107 na&nbsp;laurach. W&nbsp;pracy z&nbsp;dzie\u0107mi wa\u017Cny jest ci\u0105g\u0142y
        rozw\xF3j \u2013 nie&nbsp;dla siebie, ale przede wszystkim dla&nbsp;nich, dla&nbsp;dzieci, z&nbsp;kt\xF3rymi
        pracujemy. Wszystko to, co robimy dla&nbsp;nich, wraca do&nbsp;nas cz\u0119sto z&nbsp;podw\xF3jn\u0105 energi\u0105, daj\u0105c nam
        \u201Ekopa\u201D na&nbsp;poszerzanie swoich kwalifikacji i&nbsp;ci\u0105g\u0142e zdobywanie nowych do\u015Bwiadcze\u0144.
      `},{title:"Prywatnie",content:`W&nbsp;\u017Cyciu prywatnym czas wolny sp\u0119dzam z&nbsp;dobr\u0105 ksi\u0105\u017Ck\u0105 i&nbsp;herbat\u0105.',
           'Bardzo lubi\u0119 gotowa\u0107 i&nbsp;zaskakiwa\u0107 nowymi przepisami mojego m\u0119\u017Ca oraz&nbsp;bliskich.',
          'Wraz z&nbsp;m\u0119\u017Cem lubimy wsp\xF3lnie sp\u0119dza\u0107 czas na&nbsp;wyjazdach i&nbsp;podr\xF3\u017Cach po&nbsp;pi\u0119knej i&nbsp;malowniczej Polsce.',
           'Oboje kochamy zwierz\u0119ta! Od&nbsp;ponad p\xF3\u0142tora roku w&nbsp;naszym \u017Cyciu pojawi\u0142 si\u0119 kocurek rasy Ragdoll.`},{title:"Do\u015Bwiadczenie zawodowe",content:`
        Od&nbsp;ponad dw\xF3ch lat pracuj\u0119 na&nbsp;ca\u0142y etat w&nbsp;kaliskiem Domu Dziecka. Praca ta&nbsp;utwierdzi\u0142a mnie w&nbsp;przekonaniu,
        \u017Ce&nbsp;pomoc dzieciom to w\u0142a\u015Bnie to, co chc\u0119 w&nbsp;\u017Cyciu robi\u0107. Pomimo m\u0142odego wieku wci\u0105\u017C d\u0105\u017C\u0119 do&nbsp;poszerzania
        swojego dorobku zawodowego oraz edukacyjnego, aby w&nbsp;jeszcze bardziej wykwalifikowany spos\xF3b s\u0142u\u017Cy\u0107
        dzieciom rad\u0105 i&nbsp;wsparciem. Przez ostatni rok prowadzi\u0142am r\xF3wnie\u017C zaj\u0119cia z&nbsp;jogi dla&nbsp;najm\u0142odszych.
      `}],qualifications:{education:["Pedagogika opieku\u0144czo-wychowawcza z&nbsp;wychowaniem do&nbsp;\u017Cycia w&nbsp;rodzinie, Uniwersytet Adama Mickiewicza w&nbsp;Poznaniu, Wydzia\u0142 Artystyczno-Pedagogiczny w&nbsp;Kaliszu \u2013 studia licencjackie","Pedagogika opieku\u0144czo-wychowawcza, Uniwersytet Adama Mickiewicza w&nbsp;Poznaniu, Wydzia\u0142 Artystyczno-Pedagogiczny w&nbsp;Kaliszu \u2013 studia magisterskie","Integracja sensoryczna \u2013 diagnoza i&nbsp;terapia dzieci, Wy\u017Csza Szko\u0142a Kszta\u0142cenia Zawodowego we&nbsp;Wroc\u0142awiu \u2013 studia podyplomowe","Oligofrenopedagogika z&nbsp;elementami arteterapii, Wy\u017Csza Szko\u0142a Kszta\u0142cenia Zawodowego we&nbsp;Wroc\u0142awiu \u2013 studia podyplomowe (w&nbsp;trakcie)"]}},{id:4,seoData:{title:"Marta K\u0119sicka - Terapia Gabinet R\xF3wnowaga",metaDescription:"Marta K\u0119sicka - pedagog, terapeutka SI w Centrum R\xF3wnowaga. Specjalizuje si\u0119 w terapii ruchowej i wsparciu umiej\u0119tno\u015Bci spo\u0142ecznych dzieci."},name:"Marta K\u0119sicka",role:"Pedago\u017Cka, terapeutka w&nbsp;zakresie rehabilitacji ruchowej, terapeutka integracji&nbsp;sensorycznej, trenerka umiej\u0119tno\u015Bci&nbsp;spo\u0142ecznych, instruktorka pilates, trenerka personalna.",webpImg:"./assets/img/awatar-damski.webp",alterImg:"assets/img/awatar-damski.png",assistance:["Terapia z&nbsp;zakresu stymulacji zmys\u0142\xF3w i&nbsp;integracji sensorycznej","Terapia ruchem, kinezyterapia","Korekcja wad postawy"],aboutMe:`
      Ka\u017Cdy cz\u0142owiek to&nbsp;unikalna jednostka posiadaj\u0105ca sw\xF3j wyj\u0105tkowy zesp\xF3\u0142 cech, umiej\u0119tno\u015Bci, predyspozycji zar\xF3wno psychicznych, jak&nbsp;i&nbsp;fizycznych. Dlatego w&nbsp;swojej pracy z&nbsp;dzie\u0107mi i&nbsp;doros\u0142ymi staram si\u0119 holistycznie patrze\u0107 na&nbsp;drugiego cz\u0142owieka. Do&nbsp;ka\u017Cdej osoby podchodz\u0119 indywidualnie, uwzgl\u0119dniaj\u0105c jej mo\u017Cliwo\u015Bci, potrzeby oraz&nbsp;trudno\u015Bci. W&nbsp;pracy terapeuty \u0142\u0105cz\u0119 elementy terapii SI, rehabilitacji i&nbsp;pilatesu. Wa\u017Cna jest dla&nbsp;mnie r\xF3wnowaga psychiczna, dlatego stosuj\u0119 r\xF3wnie\u017C techniki oddechowe, kt\xF3re pomagaj\u0105 w&nbsp;relaksacji i&nbsp;obni\u017Caniu napi\u0119cia. Kieruj\u0105c si\u0119 filozofi\u0105 TSR (terapii skoncentrowanej na&nbsp;rozwi\u0105zaniach), pracuj\u0119 na&nbsp;zasobach zar\xF3wno psychicznych, jak&nbsp;i&nbsp;fizycznych, co&nbsp;pozytywnie wp\u0142ywa na&nbsp;funkcjonowanie i&nbsp;rozw\xF3j drugiego cz\u0142owieka.
    `,sections:[{title:"Relacja z&nbsp;dzieckiem \u2013 klucz do&nbsp;sukcesu",content:`
          Wa\u017Cnym aspektem pracy, zw\u0142aszcza z&nbsp;dzieckiem, jest budowanie pozytywnych relacji opartych na&nbsp;\u017Cyczliwo\u015Bci i&nbsp;empatii. S\u0105&nbsp;one niezb\u0119dne, aby&nbsp;dziecko czu\u0142o si\u0119 bezpiecznie i&nbsp;komfortowo, co&nbsp;przek\u0142ada si\u0119 na&nbsp;efektywno\u015B\u0107 terapii oraz&nbsp;ch\u0119\u0107 wsp\xF3\u0142pracy ze&nbsp;strony dzieci.
        `},{title:"Nieustanny rozw\xF3j zawodowy",content:`
          W&nbsp;\u017Cyciu zawodowym i&nbsp;prywatnym kieruj\u0119 si\u0119 zasad\u0105 Kaizen: \u201Eulepszania nie&nbsp;ma ko\u0144ca\u201D. Dlatego te\u017C rozw\xF3j, doskonalenie swoich umiej\u0119tno\u015Bci oraz&nbsp;zdobywanie wiedzy s\u0105&nbsp;dla&nbsp;mnie niezwykle wa\u017Cne, aby&nbsp;skutecznie pomaga\u0107 naszym ma\u0142ym klientom.
        `},{title:"Do\u015Bwiadczenie zawodowe",content:`
          Obecnie pracuj\u0119 w&nbsp;szkole podstawowej i&nbsp;ponadpodstawowej jako psycholog, prowadz\u0105c konsultacje, trening umiej\u0119tno\u015Bci spo\u0142ecznych oraz&nbsp;liczne warsztaty. Organizowa\u0142am tak\u017Ce gimnastyk\u0119 korekcyjn\u0105 i&nbsp;zaj\u0119cia ruchowe dla&nbsp;dzieci. Od&nbsp;5 lat prowadz\u0119 zaj\u0119cia fitness, pilates oraz&nbsp;zdrowy kr\u0119gos\u0142up. Mam do\u015Bwiadczenie w&nbsp;pracy z&nbsp;dzie\u0107mi, doros\u0142ymi i&nbsp;seniorami, zar\xF3wno grupowo, jak&nbsp;i&nbsp;indywidualnie. W&nbsp;swojej pracy \u0142\u0105cz\u0119 aspekty fizyczne i&nbsp;psychiczne, poniewa\u017C wierz\u0119, \u017Ce&nbsp;obie te&nbsp;sfery si\u0119 przenikaj\u0105. Moj\u0105 ulubion\u0105 form\u0105 ruchu jest pilates (body&amp;mind). Uwielbiam pracowa\u0107 z&nbsp;dzie\u0107mi, gdy\u017C&nbsp;praca ta&nbsp;wymaga du\u017Cej uwa\u017Cno\u015Bci, ale&nbsp;przynosi r\xF3wnie\u017C wiele satysfakcji i&nbsp;rado\u015Bci.
        `},{title:"Prywatnie",content:`
          W&nbsp;\u017Cyciu prywatnym jestem mam\u0105 \u2013 mam c\xF3rk\u0119 i&nbsp;dw\xF3ch syn\xF3w. Oko\u0142o 5 lat temu nasza rodzina powi\u0119kszy\u0142a si\u0119 o&nbsp;pieska, cudownego kundelka. Lubimy sp\u0119dza\u0107 razem czas, graj\u0105c w&nbsp;gry planszowe oraz&nbsp;chodz\u0105c na&nbsp;spacery. Uwielbiam spacerowa\u0107 po&nbsp;g\xF3rach i&nbsp;lesie.
        `}],qualifications:{education:["Pedagogika opieku\u0144czo-wychowawcza z&nbsp;promocj\u0105 zdrowia, Wy\u017Csza Szko\u0142a Informatyki \u2013 licencjat","Pedagogika terapeutyczna z&nbsp;rehabilitacj\u0105 ruchow\u0105, Akademia Humanistyczno-Ekonomiczna w&nbsp;\u0141odzi \u2013 studia magisterskie","Stymulacja zmys\u0142\xF3w i&nbsp;integracja sensoryczna, WSB-NLU w&nbsp;Nowym S\u0105czu \u2013 studia podyplomowe","Psychologia, WSB-NLU w&nbsp;Nowym S\u0105czu \u2013 studia magisterskie (w&nbsp;trakcie)","Psychologia transportu, WSB-NLU w&nbsp;Nowym S\u0105czu \u2013 studia podyplomowe (w&nbsp;trakcie)"],certificates:["Trener Umiej\u0119tno\u015Bci Spo\u0142ecznych i&nbsp;\u017Byciowych \u2013 Centrum Szkoleniowo-Terapeutyczne SENS","Pilates instruktor \u2013 Polskie Centrum Fitness","Szko\u0142a skoncentrowana na&nbsp;dobrostanie \u2013 Edulabor","Pilates Mat 1 \u2013 Open Mind Pilates School","Pilates Intermediate \u2013 Open Mind Pilates School","Pilates Body Ball \u2013 Open Mind Pilates School","Pilates dla&nbsp;Ci\u0119\u017Carnych i&nbsp;Os\xF3b starszych \u2013 Open Mind Pilates School","Zdrowy Kr\u0119gos\u0142up Tour \u2013 Open Mind Pilates School","Pilates Rollers \u2013 Open Mind Pilates School","Metody i&nbsp;Techniki Treningu Online \u2013 Online Coaching, Personal Training Academy PTA","Certified Gym Instructor \u2013 Personal Training Academy PTA","Certified Personal Trainer \u2013 Personal Training Academy PTA","Specjalista masa\u017Cu klasycznego i&nbsp;odnowy biologicznej"],specialistTrainings:["Terapia skoncentrowana na&nbsp;rozwi\u0105zaniach \u2013 poziom podstawowy, Centrum TSR","Trening zast\u0119powania agresji \u2013 Niepubliczny O\u015Brodek Doskonalenia Nauczycieli Cognitus","\u201EUcze\u0144 z&nbsp;ASD i&nbsp;nadpobudliwo\u015Bci\u0105 psychoruchow\u0105 w&nbsp;klasie. Zasady pracy i&nbsp;praktyczne wskazania.\u201D \u2013 Promade Sp. z&nbsp;o.o.","Mediacje w&nbsp;szkole jako forma rozwi\u0105zywania konflikt\xF3w \u2013 ODN w&nbsp;Kaliszu","\u201ERozwijanie kompetencji spo\u0142ecznych uczni\xF3w \u2013 wsp\xF3\u0142czesne wyzwania nauczycieli.\u201D \u2013 Promade Sp. z&nbsp;o.o.","Seminarium #Dusza#Cia\u0142o \u2013 jak&nbsp;dba\u0107 o&nbsp;swoje zdrowie psychiczne \u2013 Uniwersytet Kaliski","\u201ES\u0105 wi\u0119zy i&nbsp;wi\u0119zi. O&nbsp;neuronalnych w\u0119z\u0142ach mi\u0142o\u015Bci.\u201D \u2013 Fundacja Dbam o&nbsp;m\xF3j zasi\u0119g","Samoakceptacja jako determinant przysz\u0142ych zachowa\u0144 \u2013 Uniwersytet Kaliski"]}}],this.activeTherapistSubscription=this.activeTherapistIndex.subscribe()}setActiveTherapistId(a){a>=0&&a<this.therapists.length?this.activeTherapistIndex.next(a):console.error("Invalid therapist ID:",a)}getActiveTherapistObservable(){return this.activeTherapistIndex.asObservable().pipe(o(a=>this.therapists[a]))}getTherapists(){return this.therapists}nextTherapist(){let i=(this.activeTherapistIndex.getValue()+1)%this.therapists.length;this.activeTherapistIndex.next(i),this.goToActiveTherapist(i)}prevTherapist(){let i=(this.activeTherapistIndex.getValue()-1+this.therapists.length)%this.therapists.length;this.activeTherapistIndex.next(i),this.goToActiveTherapist(i)}goToActiveTherapist(a){let i=this.therapists[a];this.router.navigate([this.appPaths.THERAPIST_TEMPLATE,i.id])}ngOnDestroy(){this.activeTherapistSubscription&&this.activeTherapistSubscription.unsubscribe()}static{this.\u0275fac=function(i){return new(i||e)(p(c))}}static{this.\u0275prov=s({token:e,factory:e.\u0275fac,providedIn:"root"})}}return e})();export{b as a};
